import React, { Component } from "react";
import "./App.css";
import Header from "./components/header/Header";
import videodata from "./data/video-details.json";
import VideoList from "./components/nextvideos/VideoList";
import VideoPlayer from "./components/video-player/VideoPlayer";
import CommentForm from "./components/comment-form/CommentForm";
import CommentList from "./components/comment-list/CommentList";
import VideoDetails from "./components/hero-video-details/VideoDetails";
// import Hero from "./components/hero/Hero";

// array of videos
const videodetails = videodata;

// filtered array of videos that does not include first video in initial state
const sortedVidList = videodetails.filter((videos) => {
  return videos !== videodetails[0];
});

export default class App extends Component {
  state = {
    videoData: sortedVidList,
    heroData: videodetails[0],
  };

  changeVideo = (event, id) => {
    event.preventDefault();
    const clickedVideoId = this.state.videoData.findIndex(
      (videoData) => id === videoData.id
    );

    const currentHero = this.state.heroData;

    const updatedList = () => {
      this.state.videoData.filter((list) => list !== currentHero);
    };

    this.setState({
      heroData: this.state.videoData[clickedVideoId],
      videoData: updatedList,
    });
  };

  render() {
    return (
      <div className="App">
        <Header />
        <VideoPlayer
          video={this.state.videoData}
          heroVideo={this.state.heroData}
        />
        <VideoDetails video={this.state.heroData} />
        <CommentForm />
        <CommentList heroVideo={this.state.heroData} />
        <VideoList
          video={this.state.videoData}
          heroVideo={this.state.heroData}
          changeVideo={this.changeVideo}
        />
      </div>
    );
  }
}
