import React, { Component } from "react";
import "./HomePage.scss";
import VideoPlayer from "../../components/video-player/VideoPlayer";
import Main from "../../components/main/Main";
import axios from "axios";

const videoListAPI = `https://project-2-api.herokuapp.com/videos`;
const APIKey = "?api_key=02e4d4cc-df22-4061-b063-e1c63073a3fa";

export default class HomePage extends Component {
  state = {
    videoData: [],
    heroData: null,
    isLoading: true,
  };

  componentDidMount() {
    axios
      .get(`${videoListAPI}${APIKey}`)
      .then((res) => {
        const videos = res.data;
        this.setState({ videoData: videos });
        return axios
          .get(`${videoListAPI}/${videos[0].id}/${APIKey}`)
          .then((res) => {
            const video = res.data;
            this.setState({ heroData: video, isLoading: false });
          });
      })
      .catch((err) => console.log(err));
  }

  // changeVideo = (videoObj) => {
  //   const newVideo = this.state.videoData.find((video) => {
  //     return video.id === videoObj.id;
  //   });
  //   console.log(this.state.heroData);
  //   // this.setState({
  //   //   heroData: this.state.videoData[clickedVideoId],
  //   // });
  // };

  componentDidUpdate(prevProps, prevState) {
    console.log(prevState, "I'm previous state");
    console.log(this.state, "Im current state");
    console.log(prevProps, "I'm previous props");
    console.log(this.props, "I'm current props");

    if (prevState.heroData !== this.state.heroData) {
      // const newVideo = this.state.heroData.find((video) => {
      //   return video.id === this.state.heroData.id;
      // });
      console.log(this.state.heroData);

      // axios(`${videoListAPI}/${}`)
    }
    // axios call
  }

  render() {
    if (!this.state.isLoading) {
      return (
        <>
          <VideoPlayer
            video={this.state.videoData}
            heroVideo={this.state.heroData}
          />
          <Main
            video={this.state.videoData}
            heroVideo={this.state.heroData}
            changeVideo={this.changeVideo}
          />
        </>
      );
    } else {
      return <p>Loading...</p>;
    }
  }
}
