import React, { Component } from "react";
import "./App.css";
import { BrowserRouter as Router, Route, Link, Switch } from "react-dom";
import videodata from "./data/video-details.json";
import Header from "./components/header/Header";
import UploadPage from "./pages/UploadPage/UploadPage";
import HomePage from "./pages/HomePage/HomePage";

// array of videos
const videodetails = videodata;

export default class App extends Component {
  state = {
    videoData: videodetails,
    heroData: videodetails[0],
  };

  changeVideo = (id) => {
    const clickedVideoId = this.state.videoData.findIndex(
      (videoData) => id === videoData.id
    );

    this.setState({
      heroData: this.state.videoData[clickedVideoId],
    });
  };

  render() {
    return (
      <div className="App">
        <Header />
        <Router>
          <Switch>
            <Route
              path="/"
              render={(reactRouterDomProps) => {
                return (
                  <HomePage
                    {...reactRouterDomProps}
                    video={this.state.videoData}
                    heroVideo={this.state.heroData}
                    changeVideo={this.changeVideo}
                  />
                );
              }}
            />
          </Switch>
        </Router>
      </div>
    );
  }
}
