import React from "react";
import "./CommentCard.scss";

export default function CommentCard(props) {
  {
    this.props.heroVideo.comments.map((comment) => {
      return (
        <div className="comment-container">
          <h4 className="comment-container__name">{comment.name}</h4>
          <h5 className="comment-container__timestamp">{comment.timestamp}</h5>
          <p className="comment-container__comment">{comment.comment}</p>
        </div>
      );
    });
  }
}
